changeSet.sql:
CREATE TABLE users (
id uuid PRIMARY KEY,
name text,
surname text,
email text NOT NULL,
phone text,
password text NOT NULL
);

CREATE TABLE expense_categories(
id uuid PRIMARY KEY,
name text
);

CREATE TABLE default_expense_categories(
id uuid PRIMARY KEY,
name text
);

CREATE TABLE expense_currencies (
id uuid PRIMARY KEY,
name text NOT NULL,
symbol text NOT NULL
);

CREATE TABLE expenses (
id uuid PRIMARY KEY,
name text,
description text,
currency_id uuid REFERENCES expense_currencies(id),
expense_categories uuid REFERENCES expense_categories(id)
);

CREATE TABLE tags (
id uuid PRIMARY KEY,
name text
);

CREATE TABLE tags_conn_expenses (
expense_id uuid REFERENCES expenses(id),
tag_id uuid  REFERENCES tags(id)
);
